    gameId bigint,
    divisionId bigint,
    homeTeamId bigint,
    awayTeamId bigint,
    dateTime timestamp,
    venueId bigint,
    homeTeamRating int,
    awayTeamRating int,
    referee1Id bigint,
    referee2Id bigint
